package com.skysport.inerfaces.bean;/** * */public class BomInfo {    private String id;    /**     * 项目编号     */    private String projectId;    /**     *     */    private String bomId;    /**     *     */    private String bomName;    /**     * 客户     */    private String customerId;    /**     * 区域     */    private String areaId;    /**     * 系列     */    private String seriesId;    /**     * 款式     */    private String collectionNum;    /**     * 主颜色     */    private String mainColor;    /**     * 面料交货时间     */    private String fabricsEndDate;    /**     * 辅料交货时间     */    private String accessoriesEndDate;    /**     * 成衣报价时间     */    private String preOfferDate;    /**     * 成衣收到时间     */    private String clothReceivedDate;    /**     * 订单数量     */    private int offerAmount;    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getProjectId() {        return projectId;    }    public void setProjectId(String projectId) {        this.projectId = projectId;    }    public String getBomId() {        return bomId;    }    public void setBomId(String bomId) {        this.bomId = bomId;    }    public String getBomName() {        return bomName;    }    public void setBomName(String bomName) {        this.bomName = bomName;    }    public String getCustomerId() {        return customerId;    }    public void setCustomerId(String customerId) {        this.customerId = customerId;    }    public String getAreaId() {        return areaId;    }    public void setAreaId(String areaId) {        this.areaId = areaId;    }    public String getSeriesId() {        return seriesId;    }    public void setSeriesId(String seriesId) {        this.seriesId = seriesId;    }    public String getMainColor() {        return mainColor;    }    public void setMainColor(String mainColor) {        this.mainColor = mainColor;    }    public String getFabricsEndDate() {        return fabricsEndDate;    }    public void setFabricsEndDate(String fabricsEndDate) {        this.fabricsEndDate = fabricsEndDate;    }    public String getAccessoriesEndDate() {        return accessoriesEndDate;    }    public void setAccessoriesEndDate(String accessoriesEndDate) {        this.accessoriesEndDate = accessoriesEndDate;    }    public String getCollectionNum() {        return collectionNum;    }    public void setCollectionNum(String collectionNum) {        this.collectionNum = collectionNum;    }    public String getPreOfferDate() {        return preOfferDate;    }    public void setPreOfferDate(String preOfferDate) {        this.preOfferDate = preOfferDate;    }    public String getClothReceivedDate() {        return clothReceivedDate;    }    public void setClothReceivedDate(String clothReceivedDate) {        this.clothReceivedDate = clothReceivedDate;    }    public int getOfferAmount() {        return offerAmount;    }    public void setOfferAmount(int offerAmount) {        this.offerAmount = offerAmount;    }}