package com.skysport.inerfaces.bean.system;import com.skysport.core.bean.SelectItem;/** * */public class MaterialClassicInfo extends SelectItem {    private String id;    private String classicId;    private String classicName;    private String materialTypeId;    private String materialTypeName;    private int delFlag;    private String updateTime;    private String remark;    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getClassicId() {        return classicId;    }    public void setClassicId(String classicId) {        this.classicId = classicId;    }    public String getClassicName() {        return classicName;    }    public void setClassicName(String classicName) {        this.classicName = classicName;    }    public String getMaterialTypeId() {        return materialTypeId;    }    public void setMaterialTypeId(String materialTypeId) {        this.materialTypeId = materialTypeId;    }    public String getMaterialTypeName() {        return materialTypeName;    }    public void setMaterialTypeName(String materialTypeName) {        this.materialTypeName = materialTypeName;    }    public int getDelFlag() {        return delFlag;    }    public void setDelFlag(int delFlag) {        this.delFlag = delFlag;    }    public String getUpdateTime() {        return updateTime;    }    public void setUpdateTime(String updateTime) {        this.updateTime = updateTime;    }    public String getRemark() {        return remark;    }    public void setRemark(String remark) {        this.remark = remark;    }}