package com.skysport.inerfaces.bean.system;import com.skysport.core.bean.SelectItem;/** * 年份配置信息 */public class YearConfInfo extends SelectItem {    private String id;    private String yearId;    private String yearName;    private String yearCode;    private int delFlag;    private String updateTime;    private String remark;    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getYearId() {        return yearId;    }    public void setYearId(String yearId) {        this.yearId = yearId;    }    public String getYearName() {        return yearName;    }    public void setYearName(String yearName) {        this.yearName = yearName;    }    public String getYearCode() {        return yearCode;    }    public void setYearCode(String yearCode) {        this.yearCode = yearCode;    }    public int getDelFlag() {        return delFlag;    }    public void setDelFlag(int delFlag) {        this.delFlag = delFlag;    }    public String getUpdateTime() {        return updateTime;    }    public void setUpdateTime(String updateTime) {        this.updateTime = updateTime;    }    public String getRemark() {        return remark;    }    public void setRemark(String remark) {        this.remark = remark;    }}