mapper.select:
SELECT
FROM t_bominfo a WHERE a.del_flag=0
and ( bom_name like concat('%',#{searchValue},'%') or bom_id like concat('%',#{searchValue},'%') or customer_id like concat('%',#{searchValue},'%') or remark like concat('%',#{searchValue},'%') )
ORDER BY ${orderColumn} ${orderDir}
limit #{start} ,#{length}

mapper.update:
update t_bominfo
set  del_flag=1
where bom_id =#{natrualkey}
