mapper.select:
SELECT
FROM t_category WHERE del_flag=0
and ( category_id like concat('%',#{searchValue},'%') or category_name like concat('%',#{searchValue},'%') or level_id like concat('%',#{searchValue},'%') or remark like concat('%',#{searchValue},'%') )
ORDER BY ${orderColumn} ${orderDir}
limit #{start} ,#{length}

mapper.update:
update t_category 	set
category_name=#{name},
level_id = #{levelId},
remark=#{remark}
WHERE category_id =#{natrualkey}

update t_category
set  del_flag=1
where category_id =#{natrualkey}
