mapper.select:
SELECT
FROM t_area a,t_customer b WHERE a.del_flag=0 and a.customer_id = b.customer_id
and ( area_name like concat('%',#{searchValue},'%') or area_id like concat('%',#{searchValue},'%') or customer_id like concat('%',#{searchValue},'%') or remark like concat('%',#{searchValue},'%') )
ORDER BY ${orderColumn} ${orderDir}
limit #{start} ,#{length}

mapper.update:
update t_area 	set
area_name=#{name},
customer_id=#{customerId},
remark=#{remark}
WHERE area_id =#{natrualkey}

update t_area
set  del_flag=1
where area_id =#{natrualkey}
