mapper.select:
SELECT

FROM t_fabrics
WHERE del_flag=0

    and (
    fabrics_name like concat('%',#{searchValue},'%') or
    sp_id like concat('%',#{searchValue},'%') or
    remark like concat('%',#{searchValue},'%')
    )


    ORDER BY ${orderColumn} ${orderDir}

limit #{start} ,#{length}

mapper.update:
update t_fabrics
set  del_flag=1
where fabrics_id =#{natrualkey}

mapper.select:
SELECT fabrics_id
FROM t_fabrics a
WHERE a.bom_id = #{natrualkey}

mapper.update:
update t_fabrics_detail
set  del_flag=1
where fabrics_id =#{natrualkey}

update t_material_unit_dosage
set  del_flag=1
where material_id =#{natrualkey}

update t_material_spinfo
set  del_flag=1
where fabrics_id =#{natrualkey}
