mapper.select:
SELECT

FROM t_fabrics
WHERE del_flag=0

    and (
    fabrics_name like concat('%',#{searchValue},'%') or
    sp_id like concat('%',#{searchValue},'%') or
    remark like concat('%',#{searchValue},'%')
    )


    ORDER BY ${orderColumn} ${orderDir}

limit #{start} ,#{length}

mapper.update:
update t_fabrics
set  del_flag=1
where fabrics_id =#{natrualkey}
