mapper.select:
SELECT
FROM t_factory WHERE del_flag=0
and ( factory_name like concat('%',#{searchValue},'%') or factory_contact like concat('%',#{searchValue},'%') or email like concat('%',#{searchValue},'%') or remark like concat('%',#{searchValue},'%') )
ORDER BY ${orderColumn} ${orderDir}
limit #{start} ,#{length}

mapper.update:
update t_factory
set  del_flag=1
where factory_id =#{natrualkey}
