mapper.select:
SELECT

FROM t_dye
WHERE del_flag=0

    and (
    dye_name like concat('%',#{searchValue},'%') or
    remark like concat('%',#{searchValue},'%')
    )


    ORDER BY ${orderColumn} ${orderDir}

limit #{start} ,#{length}

mapper.update:
update t_dye 	set
dye_name=#{name},
remark=#{remark}
WHERE dye_id =#{natrualkey}

update t_dye
set  del_flag=1
where dye_id =#{natrualkey}
