mapper.select:
SELECT

FROM t_material_membrane_coating
WHERE del_flag=0

    and (
    momc_name like concat('%',#{searchValue},'%') or
    remark like concat('%',#{searchValue},'%')
    )


    ORDER BY ${orderColumn} ${orderDir}

limit #{start} ,#{length}

mapper.update:
update t_material_membrane_coating 	set
momc_name=#{name},
remark=#{remark}
WHERE momc_id =#{natrualkey}

update t_material_membrane_coating
set  del_flag=1
where momc_id =#{natrualkey}
