mapper.select:
SELECT

FROM t_material_position
WHERE del_flag=0

	and (
	position_id like concat('%',#{searchValue},'%') or
	position_name	like concat('%',#{searchValue},'%') or
	remark like concat('%',#{searchValue},'%') or
	)


	ORDER BY ${orderColumn} ${orderDir}

limit #{start} ,#{length}

mapper.update:
update t_material_position 	set
position_name=#{positionId},
remark=#{remark}
WHERE pantone_id =#{positionId}

update t_material_position
set  del_flag=1
where pantone_id =#{pantoneId}
