mapper.select:
SELECT

FROM t_material_unit
WHERE del_flag=0

	and (
	unit_id like concat('%',#{searchValue},'%') or
	unit_name	like concat('%',#{searchValue},'%') or
	remark like concat('%',#{searchValue},'%')
	)


	ORDER BY ${orderColumn} ${orderDir}

limit #{start} ,#{length}

mapper.update:
update t_material_unit 	set
unit_name=#{natrualkey},
remark=#{remark}
WHERE unit_id =#{natrualkey}

update t_material_unit
set  del_flag=1
where unit_id =#{natrualkey}
