mapper.select:
SELECT

FROM t_membrane_thickness
WHERE del_flag=0

    and (
    mt_name like concat('%',#{searchValue},'%') or
    remark like concat('%',#{searchValue},'%')
    )


    ORDER BY ${orderColumn} ${orderDir}

limit #{start} ,#{length}

mapper.update:
update t_membrane_thickness 	set
mt_name=#{name},
remark=#{remark}
WHERE mt_id =#{natrualkey}

update t_membrane_thickness
set  del_flag=1
where mt_id =#{natrualkey}
