mapper.select:
SELECT

FROM t_workmanship_blc
WHERE del_flag=0

    and (
    woblc_name like concat('%',#{searchValue},'%') or
    remark like concat('%',#{searchValue},'%')
    )


    ORDER BY ${orderColumn} ${orderDir}

limit #{start} ,#{length}

mapper.update:
update t_workmanship_blc 	set
woblc_name=#{name},
remark=#{remark}
WHERE woblc_id =#{natrualkey}

update t_workmanship_blc
set  del_flag=1
where woblc_id =#{natrualkey}
