mapper.select:
SELECT

FROM t_sex
WHERE del_flag=0

    and (
    sex_name like concat('%',#{searchValue},'%') or
    en_sex_name	like concat('%',#{searchValue},'%') or
    remark like concat('%',#{searchValue},'%')
    )


    ORDER BY ${orderColumn} ${orderDir}

limit #{start} ,#{length}

mapper.update:
update t_sex
set  del_flag=1
where sex_id =#{natrualkey}
