mapper.select:
SELECT

    FROM t_jc_year_conf
    WHERE del_flag=0

        and (
        year_name like concat('%',#{searchValue},'%') or
        year_code	like concat('%',#{searchValue},'%') or
        remark like concat('%',#{searchValue},'%')
        )


        ORDER BY ${orderColumn} ${orderDir}

    limit #{start} ,#{length}

mapper.update:
update t_jc_year_conf
set
year_code=#{yearCode},
year_name=#{name},
remark=#{remark}
WHERE year_id =#{natrualkey}

update t_jc_year_conf
set  del_flag=1
where year_id =#{natrualkey}
